import React,{useState} from 'react';



const Contact=()=>{
    
   const [data,setData]=useState({
       fullname:''
   });

    const InputEvent=(event)=>{
      const {name,value}=event.target;
      setData((preVal)=>{
          return {
              ...preVal,
              [name]:value,
          }
      })
    }

    const formSubmit=(e)=>{
e.preventDefault()
alert(`${data.fullname}`)
    }


    return (
        <div>
      <div className="my-5">
      <h1 className="text-center">Contact US</h1>
      </div>
      <div className="container contact_div">
      <div className="row">
      <div className="col-md-6 col-10 mx-auto">
      <form onSubmit={formSubmit}>
      <div className="mb-3">
      <label for="exampleFormControlInput1" class="form-label">FullName</label>
    <input type="text" name="fullname" value={data.fullname} onChange={inputEvent} class="form-control" id="exampleFormControlInput1" placeholder="Enter your name"></input>
</div>
<div className="mb-3">
<label for="exampleFormControlTextarea1" class="form-label">Message</label>
<textarea  class="form-control" id="exampleFormControlInput1" row="3"></textarea>
</div>
<div class="col-12">
<button type="submit" class="btn btn-outline-primary">Submit form</button>
</div>

      </form>
      </div>
      </div>
      </div>
      </div>
    )
}


export default Contact;
